import { useState } from "react";
import { Link } from "react-router-dom";

export default function SearchPage() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const searchFunds = async () => {
    const res = await fetch(`https://api.mfapi.in/mf/search?q=${query}`);
    const data = await res.json();
    setResults(data);
  };

  return (
    <div className="min-h-screen bg-white p-8">
      <h1 className="text-3xl text-center font-bold mb-6">Search Mutual Funds</h1>
      <div className="flex justify-center mb-6">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="border p-2 rounded w-80"
          placeholder="Enter fund name..."
        />
        <button onClick={searchFunds} className="ml-2 px-4 py-2 bg-blue-600 text-white rounded">
          Search
        </button>
      </div>
      <ul className="max-w-2xl mx-auto space-y-3">
        {results.map((fund) => (
          <li key={fund.schemeCode} className="p-3 border rounded hover:shadow-md transition">
            <Link to={`/fund/${fund.schemeCode}`} className="text-blue-600 hover:underline">
              {fund.schemeName}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
