// src/pages/SearchPage.js
import React, { useState, useEffect } from 'react';

export default function SearchPage() {
  const [query, setQuery] = useState('');
  const [funds, setFunds] = useState([]);
  const [filteredFunds, setFilteredFunds] = useState([]);

  // Fetch all schemes on load
  useEffect(() => {
    fetch('https://api.mfapi.in/mf')
      .then(res => res.json())
      .then(data => {
        setFunds(data);
      })
      .catch(err => console.error('Error fetching mutual fund data:', err));
  }, []);

  // Filter search results
  useEffect(() => {
    if (query.length > 2) {
      const filtered = funds.filter(fund =>
        fund.schemeName.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredFunds(filtered.slice(0, 10)); // limit results
    } else {
      setFilteredFunds([]);
    }
  }, [query, funds]);

  return (
    <div className="min-h-screen bg-blue-50 flex flex-col items-center justify-start pt-20 px-4">
      <h1 className="text-4xl font-bold text-blue-700 mb-6">Mutual Fund Search</h1>

      <input
        type="text"
        placeholder="Search mutual funds by name..."
        className="w-full max-w-xl border border-gray-300 px-4 py-2 rounded shadow-sm mb-6"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <div className="w-full max-w-xl space-y-3">
        {filteredFunds.map(fund => (
          <div
            key={fund.schemeCode}
            className="bg-white p-4 rounded shadow hover:shadow-md transition cursor-pointer"
          >
            <h3 className="text-lg font-semibold text-gray-800">{fund.schemeName}</h3>
            <p className="text-sm text-gray-500">Scheme Code: {fund.schemeCode}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
