import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthProvider";
import { Link } from "react-router-dom";

export default function SavedFundsPage() {
  const { token } = useContext(AuthContext);
  const [savedFunds, setSavedFunds] = useState([]);

  useEffect(() => {
    const fetchSavedFunds = async () => {
      try {
        const res = await fetch("http://localhost:5001/api/funds/saved", {
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!res.ok) {
          throw new Error("Failed to fetch saved funds");
        }

        const data = await res.json();
        setSavedFunds(data.savedFunds); // expects array of fund objects
      } catch (err) {
        console.error("Error fetching saved funds:", err);
      }
    };

    if (token) fetchSavedFunds();
  }, [token]);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Your Saved Funds</h1>
      {savedFunds.length === 0 ? (
        <p>No funds saved yet.</p>
      ) : (
        <ul className="space-y-2">
          {savedFunds.map((fund) => (
            <li key={fund.schemeCode}>
              <Link
                to={`/fund/${fund.schemeCode}`}
                className="text-blue-600 hover:underline"
              >
                {fund.schemeName}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
