import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthProvider";
import { Link } from "react-router-dom";

export default function SavedFundsPage() {
  const { token } = useContext(AuthContext);
  const [fundIds, setFundIds] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5001/api/funds/saved", {
      headers: { Authorization: token },
    })
      .then((res) => res.json())
      .then((data) => setFundIds(data.savedFunds));
  }, [token]);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Your Saved Funds</h1>
      <ul className="space-y-2">
        {fundIds.map((id) => (
          <li key={id}>
            <Link to={`/fund/${id}`} className="text-blue-600 hover:underline">
              Fund Code: {id}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
